content:
{
  "config": {
    "wide_screen_mode": true
  },
  "header": {
    "title": {
      "tag": "plain_text",
      "content": "You have an appeal created by *{{.requestor}}* requesting access to **{{.resource_name}}** with role **{{.role}}**. Appeal ID: **{{.appeal_id}}**"
    }
  },
  "elements": [
    {
      "tag": "div",
      "fields": [
        {
          "is_short": true,
          "text": {
            "tag": "lark_md",
            "content": "**Provider:**\n{{.provider_type}}"
          }
        },
        {
          "is_short": true,
          "text": {
            "tag": "lark_md",
            "content": "**Resource Type:**\n{{.resource_type}}"
          }
        },
{
          "is_short": true,
          "text": {
            "tag": "lark_md",
            "content": "**Resource:**\n{{.resource_name}}"
          }
        },
        {
          "is_short": true,
          "text": {
            "tag": "lark_md",
            "content": "**Account Id:**\n{{.account_id}}"
          }
        },
{
          "is_short": true,
          "text": {
            "tag": "lark_md",
            "content": "**Role:**\n{{.role}}"
          }
        },
        {
          "is_short": true,
          "text": {
            "tag": "lark_md",
            "content": "**Duration:**\n{{.created_at}}"
          }
        },
        {
          "is_short": true,
          "text": {
            "tag": "lark_md",
            "content": "**Console link:**\nhttps://console.integration.golabs.io/requests/{{.appeal_id}}"
          }
        }
      ]
    },
    {
      "tag": "hr"
    },
    {
      "tag": "action",
      "layout": "bisected",
      "actions": [
        {
          "tag": "button",
          "text": {
            "tag": "lark_md",
            "content": "Approve"
          },
          "url":"https://console.integration.golabs.io/dataaccess/appeal_action?action=approve&appeal_id={{.appeal_id}}&approval_step={{.approval_step}}&actor={{.actor}}",
          "type":"primary"
        },
        {
          "tag": "button",
          "text": {
            "tag": "lark_md",
            "content": "Reject"
          },
          "url":"https://console.integration.golabs.io/dataaccess/appeal_action?action=reject&appeal_id={{.appeal_id}}&approval_step={{.approval_step}}&actor={{.actor}}",
          "type":"primary"
        }
      ]
    }
  ]
}
